using UnityEngine;using System.Collections;using System.IO;using System.Xml.Linq;public static class ResourceUtil{	///////////////////////////////////////////////////////////////////////////////////////////////////////////////////	// Constants	//	// Root folder	//	public const string	kFolder_Root		= "FlyffExample";	///////////////////////////////////////////////////////////////////////////////////////////////////////////////////	// Static member functions	//	// Config	//	public static XElement LoadConfig(string sName)	{		return LoadXml(string.Format("{0}/Config/{1}", kFolder_Root, sName));	}	//	// Prefab	//	public static GameObject LoadPrefab(string sName)	{		return Resources.Load<GameObject>(string.Format("{0}/Prefabs/{1}", kFolder_Root, sName));	}	//	// Utilities	//	public static XElement LoadXml(string sName)	{		TextAsset xmlAsset = Resources.Load<TextAsset>(sName);					if (xmlAsset == null)			return null;					return Util.LoadXml(xmlAsset.text);	}}